mapper.update:
update
    shop.items
set
    item_name = #{itemName}
    , price = #{price}
    , delivery_option = #{deliveryOption}
    , display_option = #{displayOption}
    , stock = #{stock}
    , manufacturer = #{manufacturer}
    , description = #{description}
    , img_src = #{imgSrc}
where
    item_sn = #{itemSn}
