mapper.insert:
insert into
    shop.orders
    (
        order_id
        , order_date
        , order_status
        , price
        , delivery_sn
        , user_sn
    )
values
    (
        #{orderId}
        , now()
        , #{orderStatus}
        , #{price}
        , #{deliverySn}
        , #{userSn}
    )
